import { useState, useEffect } from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { Link } from 'react-router-dom';


const Items = () =>{

    const [items, setItems] = useState([]);
    const [error, setError] = useState(null);
    const [loaded, setLoaded] = useState(false);

    const getItems = () => {
        axios.get('http://localhost:8080/api/item')
            .then((response) => {
                setLoaded(true);
                setItems(response.data);
            })
            .catch((error) => {
                setLoaded(true);
                setError(error);
            });
    }

    useEffect(() => {
        getItems();
    }, []);

    return (
        <div className="background">
            <Container>
                <div className="bgBlur">
                    <h1 className='landing-text'style = {{padding:'30px'}}>Items </h1>
                    <div>
                        <Row>
                            {items.map((item) => (
                                // <Col lg={3} md={6} xs={12} >
                                //     <Card key={item.id}variant="dark" style={{ margin: '0.2rem', backgroundColor: '#1e1e1f' , color: '#d7d7d9'}}>
                                //         <Card.Img variant="top" src="%PUBLIC_URL%/holder.png" 'atr' />
                                //         <Card.Body>
                                //             <Card.Title>{item.title}</Card.Title>
                                //             <Card.Text style={{ textAlign: 'justify'}}>{item.description.substring(0,150) + ".."}</Card.Text>
                                //             <Link to={item.url}>
                                //                 <Button variant="light">View item</Button>
                                //             </Link>
                                //         </Card.Body>
                                //     </Card>
                                // </Col>

                                <div className="d-flex justify-content-around">
                                <Card style={{ width: '18rem' }}>
                                    <Card.Img variant="top" src="holder.js/100px180" />
                                        <Card.Body>
                                            <Card.Title>Card Title</Card.Title>
                                            <Card.Text>
                                                Some quick example text to build on the card title and make up the
                                                bulk of the card's content.
                                            </Card.Text>
                                            <Button variant="primary">Go somewhere</Button>
                                        </Card.Body>
                                    </Card>

                                <Card style={{ width: '18rem' }}>
                                    <Card.Img variant="top" src="holder.js/100px180" />
                                    <Card.Body>
                                        <Placeholder as={item.title} animation="glow">
                                            <Placeholder xs={6} />
                                        </Placeholder>
                                        <Placeholder as={item.description} animation="glow">
                                            <Placeholder xs={7} /> <Placeholder xs={4} /> <Placeholder xs={4} />{' '}
                                            <Placeholder xs={6} /> <Placeholder xs={8} />
                                        </Placeholder>
                                        <Placeholder.Button variant="primary" xs={6} />
                                    </Card.Body>
                                </Card>
                                </div>

                            ))}
                        </Row>
                    </div>
                </div>
            </Container>


        </div >
    )
}

export default Items;
