import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import card_holder_img from "../resources/large_holder.png";
import {Link} from "react-router-dom";
import Button from "react-bootstrap/Button";
import {Carousel} from "react-bootstrap";
import * as PropTypes from "prop-types";

function ExampleCarouselImage() {
    return card_holder_img;
}

const Home = () =>{
    return (
        <Carousel>
            <Carousel.Item>
                <img src={card_holder_img} />
                <Carousel.Caption>
                    <h3>Portfolio</h3>
                    <p>A place where you can add your items</p>
                    <Link to="/add">
                        <Button variant="dark" style={{border:"1px solid #3a3e42"}}>Build</Button>
                    </Link>
                </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
                <ExampleCarouselImage src={card_holder_img} text="Second slide" />
                <Carousel.Caption>
                    <h3>Your visit card</h3>
                    <p>Whether you are an engineer, artist or service provider you portfolio is your visit card.</p>
                    <Link to="/item">
                        <Button variant="dark" style={{border:"1px solid #3a3e42"}}>Portfolios</Button>
                    </Link>
                </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
                <ExampleCarouselImage src={card_holder_img} text="Third slide" />
                <Carousel.Caption>
                    <h3>Versatile</h3>
                    <p>
                        Add, aggregate, maintain and share your portfolio items.
                    </p>
                    <Link to="/item">
                        <Button variant="dark" style={{border:"1px solid #3a3e42"}}>Manage</Button>
                    </Link>
                </Carousel.Caption>
            </Carousel.Item>
        </Carousel>
    )
}

export default Home;
