import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import card_holder_img from "../resources/holder.png";
import {Link} from "react-router-dom";
import Button from "react-bootstrap/Button";

const Home = () =>{
    return (
        <div className="background">
            <Container>
                <div className="bgBlur" style={{maxWidth:"400px"}}>
                    <h1 className='landing-text'style = {{padding:'20px', textAlign:"justify"}}>Portfolio </h1>
                    <h1 className='landing-text'style = {{padding:'20px', textAlign:"justify"}}>A common place where you can add, aggregate, maintain and share your portfolio items.</h1>
                    <h1 className='landing-text'style = {{padding:'20px', textAlign:"justify"}}>Whether you are an engineer, artist or service provider you portfolio is your visit card.</h1>
                    <div>
                        <Row>
                                <Col lg={12} md={12} xs={12} >
                                    <Card variant="dark" style={{ margin: '0.2rem', backgroundColor: '#1e1e1f' , color: '#d7d7d9'}}>
                                        <Card.Body>
                                            <h1 className='landing-text'style = {{padding:'20px'}}>A common place where you can add, aggregate, maintain and share your portfolio items.</h1>
                                            <h1 className='landing-text'style = {{padding:'20px'}}>Whether you are an engineer, artist or service provider you portfolio is your visit card.</h1>
                                            <Link to="/add">
                                                <Button variant="dark" style={{border:"1px solid #3a3e42"}}>Build your portfolio</Button>
                                            </Link>
                                        </Card.Body>
                                    </Card>
                                </Col>
                        </Row>
                    </div>
                </div>
            </Container>


        </div >
    )
}

export default Home;
