import { useState, useEffect } from "react";
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import axios from 'axios';
import { Link } from 'react-router-dom';


const Items = () =>{

    const [itemsList, setItemsList] = useState([]);
    const [error, setError] = useState(null);
    const [loaded, setLoaded] = useState(false);

    const getItems = () => {
        axios.get('http://localhost:8080/api/item')
            .then((response) => {
                setLoaded(true);
                setItemsList(response.data);
            })
            .catch((error) => {
                setLoaded(true);
                setError(error);
            });
    }

    useEffect(() => {
        getItems();
    }, []);

    return (
        <div className="background">
            <Container>
                <div className="bgBlur">
                    <h1 className='landing-text'>Items </h1>
                    <div>
                        <Row>
                            {itemsList.map((item) => (
                                <Col lg={4} xs={12} md={6} className="p-2">
                                    <div className="card-wrapper">
                                        <div className="content">

                                            <div className="face-back z-depth-2 colorScheme">
                                                <Card.Body >
                                                    <Card.Title>{item.title}</Card.Title>
                                                    <br />
                                                    <Link to={`item.url`}>
                                                        <Button className="alignBottom" value={item.url} variant="dark">View item</Button>
                                                    </Link>
                                                </Card.Body>
                                            </div>
                                        </div>
                                    </div>
                                </Col>
                            ))}
                        </Row>
                    </div>
                </div>
            </Container>
        </div >
    )
}

export default Items;
