import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import card_holder_img from "../resources/holder.png";
import {Link} from "react-router-dom";
import Button from "react-bootstrap/Button";

const Home = () =>{
    return (
        <div className="background">
            <Container>
                <div style={{textAlign: "center"}}>
                    <h1 className='landing-text' style = {{padding:'4rem', textAlign:"center", fontSize:"5em"}}>Portfolio </h1>
                </div>
                <div style={{textAlign: "center"}}>
                    <div style={{margin: "2rem", backgroundColor:"#282a2e", width:"100%", borderRadius:"30px"}}>
                        <h1 className='landing-text'style = {{padding:'2rem', textAlign:"justify", fontSize:"3em"}}>A common place where you can add, aggregate, maintain and share your portfolio items.</h1>
                        <Link to="/item">
                            <Button variant="dark" style={{border:"1px solid #3a3e42", padding:"1rem", margin:"1em"}}>See available items</Button>
                        </Link>
                    </div>
                </div>
                <div style={{textAlign: "center"}}>
                    <div style={{margin: "2rem", backgroundColor:"#282a2e", width:"100%", borderRadius:"30px"}}>
                        <h1 className='landing-text'style = {{padding:'2rem', textAlign:"justify", fontSize:"3rem"}}>Whether you are an engineer, artist or service provider you portfolio is your visit card.</h1>
                        <Link to="/add">
                            <Button variant="dark" style={{border:"1px solid #3a3e42", padding:"1rem", margin:"1rem"}}>Build your portfolio</Button>
                        </Link>
                    </div>
                </div>




            </Container>


        </div >
    )
}

export default Home;
